      out.write("    function setTotalInfo() {\r\n");
      out.write("    	let indPrice = 0;		// 개별 가격		\r\n");
      out.write("    	let totalPrice = 0;		// 총 가격\r\n");
      out.write("    	let totalCount = 0;		// 총 갯수\r\n");
      out.write("    	let totalKind = 0;		// 총 종류\r\n");
      out.write("    	let totalPoint = 0;		// 총 마일리지\r\n");
      out.write("    	let deliveryPrice = 0;	// 배송비\r\n");
      out.write("    	let usePoint = 0;		// 사용 포인트(할인가격)\r\n");
      out.write("    	let finalTotalPrice = 0;// 최종 가격(총 가격 + 배송비)\r\n");
      out.write("    	\r\n");
      out.write("    	$(\".book-data\").each(function(index, element){\r\n");
      out.write("    		// 총 갯수\r\n");
