      out.write("    		indPrice = (parseInt($(element).find(\"#price\").text()))*(parseInt($(element).find(\".count-box\").text()));\r\n");
      out.write("    		totalPrice += indPrice;\r\n");
      out.write("    		// 총 종류\r\n");
      out.write("    		totalKind += 1;\r\n");
      out.write("    		// 총 마일리지\r\n");
