      out.write("    	} else if(totalPrice == 0){\r\n");
      out.write("    		deliveryPrice = 0;\r\n");
      out.write("    	} else {\r\n");
      out.write("    		deliveryPrice = 3000;	\r\n");
      out.write("    	}\r\n");
